import {
	FETCH_ITEMS_START,
	FETCH_ITEMS_SUCCESS,
	FETCH_ITEMS_FAILURE
} from 'actionTypes'
import {fetchItems as fetchItemsApi} from 'api'

export const fetchItems = () => async dispatch => {
	dispatch ({type: FETCH_ITEMS_START})

	try{
		const items = await fetchItemsApi()
		dispatch({
			type: FETCH_ITEMS_SUCCESS,
			payload: items
		})
	} catch (err) {
		dispatch({
			type: FETCH_ITEMS_FAILURE,
			payload: err,
			error: true
		})
	}
	
}